import React from "react";
import "./AddObject.scss";
import { useMemo } from "react";

export default function AddObject({ page }) {

  const EmployeesFields = ["name", "surname", "username", "password", "team", "availability", "securityAccess"]
  const SprintsFields = ["name", "field3"]
  const TasksFields = ["name", "project", "sprint"]
  const ProjectsFields = ["name"]

  const currentPageFields = useMemo(() => {
    switch (page) {
      case "Employees":
        return EmployeesFields;
      case "Sprints":
        return SprintsFields;
      case "Tasks":
        return TasksFields;
      case "Projects":
        return ProjectsFields;
      default:
        return []; // You can specify a default value or handle other cases as needed
    }
  }, [page]);


  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("submit", e)
    console.log(e.target[0].value)

    const dataObject = {}
    currentPageFields.forEach((field, idx) => {
      dataObject[field] = e.target[idx].value
    })

    let response = null;

    switch(page){
      case "Employees":
        dataObject["securityAccess"] = parseInt(dataObject["securityAccess"])
        dataObject["availability"] = parseInt(dataObject["availability"])
        break;
      case "Sprints":
        break;
      case "Tasks":
        break;
      case "Projects":
        break;
      default:
        return [];
    }

    console.log(dataObject)

  }


  console.log(currentPageFields)
  return (
    <div className="addObjPage">
      <form onSubmit={handleSubmit}>
        <h2>Add New {page.slice(0, -1)} </h2>

        {currentPageFields.map(field =>
          <>
            <label>
              {field.charAt(0).toUpperCase() + field.slice(1)}:
              <input placeholder={field} />
            </label>
          </>
        )}
        <button>Add</button>
      </form>
    </div>
  );
}
