export const getUserRights = () => {
  return localStorage.getItem("rights");
};

export const RIGHTS = {
  HR: {
    addEmployee: true,
    deleteUser: true,
    updateUser: true,
    addTask: true,
    addSprint: false,
    deleteSprint: false,
    addProject: false,
  },
  "Team Leader": {
    addEmployee: false,
    deleteUser: false,
    updateUser: false,
    addTask: true,
    addSprint: true,
    deleteSprint: true,
    addProject: false,
  },
  "Department Chief": {
    addEmployee: false,
    deleteUser: false,
    updateUser: false,
    addTask: true,
    addSprint: true,
    deleteSprint: true,
    addProject: true,
  },
  Employee: {
    addEmployee: false,
    deleteUser: false,
    updateUser: false,
    addTask: true,
    addSprint: false,
    deleteSprint: false,
    addProject: false,
  },
};

export const checkRights = (action) => {
  switch (action) {
    case "add-employee":
      return RIGHTS[getUserRights()]?.addEmployee;
    case "delete-user":
      return RIGHTS[getUserRights()]?.deleteUser;
    case "update-user":
      return RIGHTS[getUserRights()]?.updateUser;
    case "add-task":
      return RIGHTS[getUserRights()]?.addTask;
    case "add-project":
      return RIGHTS[getUserRights()]?.addProject;
    case "add-sprint":
      return RIGHTS[getUserRights()]?.addSprint;
    default:
      console.log("Unknown action for rights: ", action);
      return null;
  }
};
