import React, { useState } from "react";
import "./SprintPage.scss";
import SprintSvg from "../../../assets/SprintSvg";

export default function SprintPage(props) {
  const { title, status, project, team, tasks } = props.sprint;

  const [changeStatus, setChangeStatus] = useState(false);



  const handleUpdateEmployee = async (field) => {
    let changes = {};
    switch (field) {
      case "team":
        changes["team"] = newTeam;
        break;
      case "availability":
        changes["availability"] = availability.toLowerCase() === "available" ? "On vacation" : "Available";
        break;
      case "username":
        changes["username"] = newUsername;
        break;
      case "password":
        changes["password"] = hashPassword(newPassword);
        break;
      case "securityAccess":
        changes["securityAccess"] = newSecurityAccess;
        break;
      default:
        console.log("Unknown field for update: ", field);
        return;
    }

    const updateResponse = await updateEmployee(uid, getUserData().userId, changes);

    if (updateResponse) {
      alert("Employee updated!");
      window.location.reload();
    } else {
      alert("Employee could not be updated!");
    }
  };

  return (
    <div className="SprintPage">
      <SprintSvg />
      <h2>{title}</h2>

      <div id="sprint-info">
        <div id="sprint-main-info">
          <p>
            Status:
            {!changeStatus ? (
              <span> {status}</span>
            ) : (
              <select>
                altceva
                <option>Active</option>
                <option>In progress</option>
                <option>Closed</option>
              </select>
            )}
          </p>
          <p>
            Project:
            <span> {project}</span>
          </p>
          <p>
            Team:
            <span> {team}</span>
          </p>
        </div>

        <button
          onClick={() => {
            changeStatus ? setChangeStatus(!changeStatus) : handleUpdateEmployee("status");
          }}
        >
          {changeStatus ? "Save" : "Change status"}
        </button>

        <div id="sprint-desc">
          <span>Tasks</span>
          <div>
            {tasks.split(",").map((t, idx) => (
              <p id={`task-${idx}`}>&bull; {t}</p>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}
