import { useState } from "react";
import deleteEmployee from "../../../api/deleteEmployee";
import AvatarSvg from "../../../assets/avatar";
import { getUserData } from "../../../const/utils";
import "./EmployeePage.scss";
import updateEmployee from "../../../api/updateEmployee";

export default function EmployeePage(props) {
  const {
    uid,
    name,
    tasks,
    projects,
    availability,
    email,
    phone,
    linkedin,
    team,
    surname,
    securityAccess,
  } = props.employee;

  const [changeTeam, setChangeTeam] = useState(false);
  const [newTeam, setNewTeam] = useState("");


  const handleDeleteEmployee = async (id) => {
    const deleteResponse = await deleteEmployee(id, getUserData().userId)
    console.log(deleteResponse);
    // console.log('response of deletion:', deleteResponse);
    if (deleteResponse.ok) {
      alert("Employee deleted!")
      window.location.reload()
    }
    else {
      alert("Employee could not be deleted!")
    }
  }

  const handleUpdateEmployee = async (field) => {
    const changes = field === "team" ? { team: newTeam } : { availability: newAvailability }
    const updateResponse = await updateEmployee(uid, getUserData().userId, changes)

    if (updateResponse) {
      alert("Employee updated!")
      window.location.reload()
    }
    else {
      alert("Employee could not be updated!")
    }
  }

  return (
    <div className="employeePage">
      <div id="data">
        <div id="employeeCard">
          <h2>{`${name} ${surname} `}</h2>
          <p>Security access: {securityAccess}</p>
        </div>

        <button onClick={() => { handleDeleteEmployee(uid) }}>Delete user</button>

        <div className="label">
          <span>Team:</span>
          {!changeTeam ? <>
            <span>{team}</span>
            <button onClick={() => { setChangeTeam(!changeTeam) }}>change</button>
          </> : <>
            <input onChange={(e) => { setNewTeam(e.target.value) }} />
            <button onClick={() => { handleUpdateEmployee() }}>save</button>
          </>}

        </div>

        <div className="label">
          <span>Tasks:</span>

          {
            tasks === "" ? <i>None</i> : tasks.split(",").map((t, idx) => <span key={`task-${idx}`}>{t}</span>)
          }
        </div>

        <div className="label">
          <span>Projects:</span>
          {
            projects === "" ? <i>None</i> : projects.split(",").map((p, idx) => <span key={`proj-${idx}`}>{p}</span>)
          }
        </div>

        <div className="label">
          <span>Availability:</span>
          <span>{availability}</span>

          <button>change</button>
        </div>
      </div>

      <div id="contact">
        <AvatarSvg />

      </div>
    </div>
  );
}
