import deleteEmployee from "../../../api/deleteEmployee";
import AvatarSvg from "../../../assets/avatar";
import "./EmployeePage.scss";

export default function EmployeePage(props) {
  const {
    uid,
    name,
    role,
    tasks,
    projects,
    availability,
    email,
    phone,
    linkedin,
    team,
  } = props.employee;

  const handleDeleteEmployee = async (id) => {
    const deleteResponse = await deleteEmployee(id, getUserData().userId)

    if (deleteResponse.status === 200) {
      alert("Employee deleted!")
      window.location.reload()
    }
  }

  console.log(props.employee)
  return (
    <div className="employeePage">
      <div id="data">
        <div id="employeeCard">
          <h2>{name}</h2>
          <p>{role}</p>
        </div>

        <button onClick={() => { handleDeleteEmployee(uid) }}>Delete user</button>

        <div className="label">
          <span>Team:</span>
          <span>{team}</span>
        </div>

        <div className="label">
          <span>Tasks:</span>

          {
            tasks === "" ? <i>None</i> : tasks.split(",").map((t) => <span>{t}</span>)
          }
        </div>

        <div className="label">
          <span>Projects:</span>
          {
            projects === "" ? <i>None</i> : projects.split(",").map((p) => <span>{p}</span>)
          }
        </div>

        <div className="label">
          <span>Availability:</span>
          <span>{availability}</span>

          <button>change</button>
        </div>
      </div>

      <div id="contact">
        <AvatarSvg />
        {/* <div>
          <h2>Contact information</h2>
          {email && <span>Email: {email}</span>}
          {phone && <span>Phone: {phone}</span>}
          {linkedin && <span>LinkedIn: {linkedin}</span>}
          {teams && <span>Microsoft Teams: {teams}</span>}
        </div> */}
      </div>
    </div>
  );
}
