import React from "react";
import "./AddObject.scss";
import { useMemo } from "react";
import { getUserData } from "../../../const/utils";
import createEmployee from "../../../api/createEmployee";

export default function AddObject({ page }) {

  //
  const EmployeesFields = ["N-name", "N-surname", "N-username", "N-password", "N-team", "N-availability", "D-securityAccess-HR-Team Leader-Department Chief-Employee"]
  const SprintsFields = ["N-name", "N-field3"]
  const TasksFields = ["N-name", "N-project", "N-sprint"]
  const ProjectsFields = ["N-name"]

  const currentPageFields = useMemo(() => {
    switch (page) {
      case "Employees":
        return EmployeesFields;
      case "Sprints":
        return SprintsFields;
      case "Tasks":
        return TasksFields;
      case "Projects":
        return ProjectsFields;
      default:
        return []; // You can specify a default value or handle other cases as needed
    }
  }, [page]);


  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("submit", e)
    console.log(e.target[0].value)

    const dataObject = {}
    currentPageFields.forEach((field, idx) => {
      dataObject[field] = e.target[idx].value
    })

    let response = null;
    const userId = getUserData().userId;

    switch (page) {
      case "Employees":
        dataObject["securityAccess"] = parseInt(dataObject["securityAccess"])
        dataObject["availability"] = parseInt(dataObject["availability"])
        response = await createEmployee(userId, dataObject)
        break;
      case "Sprints":
        break;
      case "Tasks":
        break;
      case "Projects":
        break;
      default:
        return [];
    }

    alert(`New ${page.slice(0, -1)} created!`)
    window.location.reload()


    console.log(response);
  }


  return (
    <div className="addObjPage">
      <form onSubmit={handleSubmit}>
        <h2>Add New {page.slice(0, -1)} </h2>

        {currentPageFields.map(field =>
          <>
            <label>
              {field.charAt(0).toUpperCase() + field.slice(1)}:
              <input placeholder={field} required defaultValue={`test-${field}`} />
            </label>
          </>
        )}
        <button>Add</button>
      </form>
    </div>
  );
}
