import React, { useState } from "react";
import "./ProjectPage.scss";
import { getUserData } from "../../../const/utils";
import { checkRights } from "../../../const/rightsUtils";
import updateProject from "../../../api/updateProject";
// import SprintSvg from "../../assets/SprintSvg";

export default function ProjectPage(props) {
  const { uid, title, status, project, team, description } = props.project;

  const [changeStatus, setChangeStatus] = useState(false);
  const [newStatus, setNewStatus] = useState("");

  const handleUpdateProject = async (field) => {
    let changes = {};
    switch (field) {
      case "status":
        changes["status"] = newStatus;
        break;
      default:
        console.log("Unknown field for update: ", field);
        return;
    }

    await updateProject(uid, getUserData().userId, changes);
    // alert("Sprint updated!");
    window.location.reload();
  };

  const handleDeleteProject = async (uid) => {
    // const deleteResponse = await deleteSprint(uid, getUserData().userId);
    // if (deleteResponse.ok) {
    //   alert("Sprint deleted!");
    //   window.location.reload();
    // } else {
    //   alert("Sprint could not be deleted!");
    // }
  };

  return (
    <div className="ProjectPage">
      {/* <SprintSvg /> */}
      <h2>{title}</h2>

      {checkRights("delete-sprint") && (
        <button
          onClick={() => {
            handleDeleteProject(uid);
          }}
        >
          Delete sprint
        </button>
      )}

      <div id="project-info">
        <div id="project-main-info">
          <p>
            Status:
            <span> {status}</span>
          </p>
          <p>
            Team:
            <span> {team}</span>
          </p>
        </div>

        <button
          onClick={() => {
            changeStatus ? handleUpdateProject("status") : setChangeStatus(!changeStatus);
          }}
        >
          {changeStatus ? "Save" : "Change status"}
        </button>

        <div id="project-desc">
          <span>Description</span>
          <div>{description}</div>
        </div>
      </div>
    </div>
  );
}
